import React, { useEffect, useState } from 'react';
import { getExpenses, getIncomes } from '../services/api';
import AddExpense from './AddExpense';
import AddIncome from './AddIncome';

const Dashboard = () => {
  const [expenses, setExpenses] = useState([]);
  const [incomes, setIncomes] = useState([]);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchData = async () => {
      const fetchedExpenses = await getExpenses(token);
      const fetchedIncomes = await getIncomes(token);
      setExpenses(fetchedExpenses);
      setIncomes(fetchedIncomes);
    };
    fetchData();
  }, [token]);

  return (
    <div className="container mx-auto mt-8">
      <h2 className="text-2xl">Dashboard</h2>
      <div className="mt-4">
        <h3 className="text-xl">Add Expense</h3>
        <AddExpense token={token} />
      </div>
      <div className="mt-4">
        <h3 className="text-xl">Add Income</h3>
        <AddIncome token={token} />
      </div>
      <div className="mt-4">
        <h3 className="text-xl">Expenses</h3>
        <ul>
          {expenses.map((expense) => (
            <li key={expense._id}>{`${expense.category}: ${expense.amount}`}</li>
          ))}
        </ul>
      </div>
      <div className="mt-4">
        <h3 className="text-xl">Incomes</h3>
        <ul>
          {incomes.map((income) => (
            <li key={income._id}>{`${income.source}: ${income.amount}`}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
